function comma(num){
    var len, point, str; 

    num = num + ""; 
    point = num.length % 3 ;
    len = num.length; 

    str = num.substring(0, point); 
    while (point < len) { 
        if (str != "") str += ","; 
        str += num.substring(point, point + 3); 
        point += 3; 
    } 

    return str;

}

//전화번호 하이픈 추가
function phone(num){
    let result = '';
    let number = num + '';
    if(number.substring(0, 2) == '02'){
      if (number.substring(2).length == 7) {
        result = number.substring(0, 2) + "-" + number.substring(2, 5) + "-" + number.substring(5);  
      }else{
        result = number.substring(0, 2) + "-" + number.substring(2, 6) + "-" + number.substring(6);
      }
    }else{
      if (number.substring(3).length == 7) {
        result = number.substring(0, 3) + "-" + number.substring(3, 6) + "-" + number.substring(6);
      }else{
        result = number.substring(0, 3) + "-" + number.substring(3, 7) + "-" + number.substring(7);
      }
    }
    return result;
}

function regdt(num){
    const date = new Date(num);
    const year = date.getFullYear();
    let month = (1 + date.getMonth());
    month = month >= 10 ? month : '0'+month;
    let day = date.getDate();
    day = day >= 10 ? day : '0'+day;
    let hour = date.getHours();
    hour = hour >= 10 ? hour : '0'+hour;
    let minutes = date.getMinutes();
    minutes = minutes >= 10 ? minutes : '0'+minutes;

    return year +'-'+ month +'-'+ day + ' ' + hour +':'+ minutes
}

