function report(type, resource_id) {
  let array
  if(type == '배달-이용자'){
    array = ['이용자가 불친절해요', '이용자가 집에 없어요']
  }else if(type == '배달-사업주'){
    array = ['사업주가 불친절해요', '시간약속을 안지켜요', '사업주가 배송준비을 안해요']
  }else{
    array = ['배송기사가 불친절해요', '배송이 늦어요', '배송기사가 배송을 안해요']
