}


function product_status(product_data) {

  let free_status = ['나눔중','나눔중지']
  let price_status = ['판매중', '판매중지']
  let status_list
  if(free_status.includes(product_data.status)) {
    status_list = free_status
  } else if(price_status.includes(product_data.status)) {
    status_list = price_status
  }

  if(status_list) {
    Jevil.popupSelect(status_list.map(m=>({key:m,value:m})),
      {selectedKey:product_data.status}, function(selectedKey){

        Jevil.startLoading()
        Jevil.postThen('/api/product/status', 
          { 
            product_id:product_data.id,
            status:selectedKey
          }, 
          function(res){
            Jevil.stopLoading()
            if(res && res.r){
              product_data.status = selectedKey
              Jevil.updateThis()
              Jevil.save('dirty_product', 'Y')
            }
          }
        )
    })  
  } else {
    Jevil.alert(product_data.status  + ' 상태는 변경 불가합니다')

  }
}


