Jevil.starRatingClick = function (thisData) {
  data.starRating = thisData.rate
  Jevil.starRatingUpdate(data)
  Jevil.update()
}

Jevil.starRatingUpdate = function (thisData) {
  if(!thisData.starRating) {
    thisData.starRating = 0
  }

  thisData.starRatingList = []
  for(let i=1;i<=5;i++){
    if(thisData.starRating >= i)
      thisData.starRatingList.push({star:'Y', rate:i})
    else
      thisData.starRatingList.push({star:'N', rate:i})
  }
}

