function complete() {
  if(data.input1 == '0000') {
    Jevil.go('join_type', {user:{phone:data.input1, n:data.input2,location:data.location}})
    return
  }

  if(data.step == '1') {
    if(data.input1 && data.input1.length >=7 && data.input1.length < 12) {
      Jevil.startLoading()
      Jevil.postThen('/api/sms/request', {phone:data.input1}, function(res) {
        Jevil.stopLoading()  
        if(res && res.r) {
          data.step = '2'
          data.input2 = ''
          Jevil.resetTimer('timer')
          data.complte_text = '인증 하기'
          Jevil.update()
        }
      })
    } else {
      Jevil.alert('휴대 전화 번호를 입력해주세요')
    }
  } else {
    if(data.input2 && data.input2.length >=4) {
      Jevil.startLoading()
      Jevil.postThen('/api/sms/auth', {phone:data.input1, number:data.input2}, function(res) {
        if(res) {
          if(res.r){
            login()
          } else {
            Jevil.stopLoading()  
            Jevil.alert(res.msg)
          }
        } else 
          Jevil.stopLoading()  
      })
    }
  }
}

function login() {
  Jevil.postThen('/member/login', {type:'phone', email:data.input1, pass:data.input2}, function(res) {
    if(res && res.r){
      Jevil.save('x-access-token', res.token)
      Jevil.save('user_type', res.member.user_type)
      Jevil.postThen('/member/location/set', data.location, function (res) {
        Jevil.stopLoading()
