  if(!data.input1){
    Jevil.alert('닉네임을 입력해주세요')
    return 
  }
  Jevil.startLoading()
  join()
}

function join() {
  let join = {
    type:'phone',
    n : data.user.n,
    name : data.input1,
    identifier : data.user.phone,
    phone : data.user.phone,
    user_type : data.user.user_type,
    location : data.user.location,
    place : data.user.place,
    agree1 : 'Y',
    agree2 : 'Y',
  }

  if(data.user.user_type == '사업주') {
    join.bank = data.user.bank
    join.bank_number = data.user.bank_number
    join.bank_name = data.user.bank_name

    join.business_name = data.user.business_name
    join.business_number = data.user.business_number
    join.business_ceo_name = data.user.business_ceo_name
    join.business_type = data.user.business_type

    join.business_place_name = data.user.place.place_name
    join.business_place_road_address_name  = data.user.place.road_address_name
    join.business_place_address_name = data.user.place.address_name
    join.business_place_address_detail = data.user.place.address_detail
    join.business_place_address_post = data.user.place.address_post

    join.business_place_address_x = data.user.place.x
    join.business_place_address_y = data.user.place.y
  }
  Jevil.postThen('/member/join', join, function(joinResponse) {
    if(joinResponse && joinResponse.r){
      login()
    } else {
      if(joinResponse)
        Jevil.alert(joinResponse.msg)
      else 
        Jevil.alert('일시적 네트워크 오류가 발생하였습니다. 다시 시도해주세요.')
      Jevil.stopLoading()
    }
  })
}


function login() {
  Jevil.startLoading()
  Jevil.postThen('/member/login', {type:'phone', email:data.user.phone, pass:data.user.n}, function(res) {
    Jevil.stopLoading()
    if(res && res.r){
      Jevil.save('x-access-token', res.token)
      Jevil.save('user_type', res.member.user_type)
      profileNickEdit()
    } else {
      if(res)
        Jevil.alert(res.msg)
      else
        Jevil.alert('일시적 네트워크 오류가 발생하였습니다. 다시 시도해주세요.')
    }
  })
}


function profileNickEdit() {
  let uploadFiles = [];
  if(!data.image.startsWith('http')) {
    uploadFiles.push(data.image)
  }
  Jevil.uploadS3(uploadFiles, function(result) {
    if(result && result.r) {
      let edit = {
        name : data.input1,
        profile: result.uploadedFile[0].key,
      }
      Jevil.postThen('/member/edit', edit, function(res) {
        Jevil.stopLoading()
        if(res && res.r){
          Jevil.rootScreen('join_end')
        } else {
          Jevil.alert('일시적 네트워크 오류가 발생하였습니다. 다시 시도해주세요.')
        }
      })
    } else {
      Jevil.stopLoading()
      Jevil.alert('일시적 네트워크 오류가 발생하였습니다.')
    }
  })
}

