  type :'filter',
  category_list : []
}

data.list = [data.tab_item, data.category_item]

updateCategory()
updateLocation()
updateChatNew()
Jevil.sendPushKeyWithDevilServer()

function search(){
  Jevil.go('search')
}

function updateLocation() {
  Jevil.getThen('/member/location/get', function(res) {
    if(res && res.r) {
      data.address3 = res.address4?res.address4:res.address3
      Jevil.update()
    }
  })
}

function tab1() {
  data.main_tab = data.tab_item.main_tab = '1'
  updateCategory()
  updateLocation()
  Jevil.update()
}
function tab2() {
  data.main_tab = data.tab_item.main_tab = '2'
  Jevil.update()
  update()
}

function menu_item(thisData) {

  for(let i=0;i<data.category_item.category_list.length;i++) {
    let m = data.category_item.category_list[i]
    if(m.id == thisData.id){
      m.selected = 'Y'
      Jevil.scrollTo('menu_filter', i, true)
    } else 
      m.selected = 'N'
  }

  // data.category_item.category_list[3].selected = 'Y'
  // thisData.selected = 'Y'
  Jevil.update()
  update()
}

function update() {
  data.page = 0
  data.nomore = false

  if(data.main_tab == '1')
    data.list = [data.tab_item, data.category_item]
  else
    data.list = [data.tab_item]
  append()

  Jevil.getThen('/api/cart/list', function(res) {
    if(res && res.r) {
      data.cartLength = parseInt(res.list.length)+'';
      data.cartCount = data.cartLength == 0 ? 'N' : 'Y'

      Jevil.update()
      Jevil.videoViewAutoPlay()
    }
  })
}

function updateCategory() {
  Jevil.startLoading()
  if(data.main_tab == '1') {
    Jevil.getThen('/api/category', function(res) {
      if(res && res.r) {
        data.category_item.category_list = res.category;
        data.category_item.category_list.splice(0,0, {id:-1, name:'전체', selected:'Y'})
        update()
        Jevil.update()
      }
    })  
  }
}

function getSelectedCategoryId() {
  //Jevil.alert(JSON.stringify(data.list[1], null, 2))
  let categorys = data.list.filter(f=>f.type =='filter')[0].category_list.filter(f=>f.selected=='Y')
  //Jevil.alert(JSON.stringify(data.list.filter(f=>f.type =='filter'), null, 2))
  if(categorys.length >0)
    return categorys[0].id
  return -1
}

function append() {
  if(data.nomore)
    return

  if(data.main_tab == 1) {
    let url = '/api/product/list?category_id=' + getSelectedCategoryId() + '&page='+parseInt(data.page)

    Jevil.startLoading()
    Jevil.getThen(url, function(res) {
      Jevil.stopLoading()
      if(res && res.r) {
        res.list.map(m=>{
          m.image_length = (parseInt(m.media.length) < 3 ?  parseInt(m.media.length) : 3)+ '';  //안드로이드에서는 자꾸 '1.0' 으로 되서 image_length == '1' 비교식이 실패한다  
          data.list.push(m)
        })
        data.nomore = res.list.length == 0
        // Jevil.alert(JSON.stringify(res.list[5], null, 2))
        data.list.length == 2 ? data.first = 'Y' : data.first = 'N';
        data.first_text = '진행 중인 오늘집밥이 없습니다.'
        Jevil.update()

        Jevil.consumeStandardReserveUrl()
      } else {
        Jevil.alert('일시적 오류가 발생하였습니다 다시 시도해주세요')
      }
    })
  } else {
    let url = '/api/recipe/list'
    Jevil.startLoading()
    Jevil.getThen(url, function(res) {
      Jevil.stopLoading()
      if(res && res.r) {
        res.list.map(m=>{
          m.address_text = m.address1 +' '+ m.address2 +' '+ m.address3 +' '+ m.address4
          data.list.push(m)
        })
