      Jevil.getThen('/api/walk-delivery/requests', function(res) {
        Jevil.stopLoading()
        if(res && res.r){
          data.no_address = my.road_address_name || my.address_name ? 'N':'Y';
          data.none = data.no_address =='N' && res.results.length == 0 ? 'Y' : 'N';
