  })
}

function complete(){


  if(data.tab=='2' && !data.input3) {
    Jevil.alert('가격을 입력해주세요')
    return 
  }

  if(data.tab=='2' && data.input3 && (parseInt(data.input3) == 0)) {
    Jevil.alert('가격을 입력해주세요')
    return 
  }

  if(data.tab=='2' && isNaN(data.input3)) {
    Jevil.alert('가격에 숫자를 입력해주세요')
    return 
  }


  if(data.list.length < 2){
    Jevil.alert('사진을 1장 이상입력해주세요')
    return 
  }

  if(!data.category_id){
    Jevil.alert('카테고리를 선택해주세요')
    return 
  }

  if(!data.input1){
    Jevil.alert('제목을 입력해주세요')
    return 
  }
  if(!data.input2){
    Jevil.alert('설명을 입력해주세요')
    return 
  }


  let product = {}
  if(data.id)
    product.id = data.id
  product.name = data.input1
  product.desc = data.input2
  product.price = data.input3
  product.media = []
  product.category_id = data.category_id;

  Jevil.startLoading()
  let uploadFiles = [];
  for(let i=1;i<data.list.length;i++){
    let item = data.list[i]
    if(item.type == 'image' && !item.image.startsWith('http'))
      uploadFiles.push(item.image)
    else if(item.type == 'video' && !item.image.startsWith('http')) {
      uploadFiles.push(item.image)
      uploadFiles.push(item.video)
    }
  }


  Jevil.uploadS3(uploadFiles, function(result) {
    //Jevil.alert(JSON.stringify(uploadFiles, null, 2))
    if(result.r) {
      for(let i=1;i<data.list.length;i++) {
        let item = data.list[i]
        if(!item.image.startsWith('http'))
          item.image = result.uploadedFile.filter(f=>f.original==item.image)[0].key
        if(item.video && !item.video.startsWith('http'))
          item.video = result.uploadedFile.filter(f=>f.original==item.video)[0].key

        product.media.push(item);
      }
      Jevil.postThen('/api/product/upsert', product, function(response) {
        Jevil.stopLoading()
        if(response.r){
          Jevil.save('dirty_product', 'Y')
          if(data.id) //수정일 경우 상품상세를 리로드한다
            Jevil.save('product_detail_dirty', 'Y')
          Jevil.alertFinish('게시글이 등록되었습니다')
        } else {
            Jevil.alert('일시적 네트워크 오류가 발생하였습니다. 다시 시도해주세요.')
        }
      })
    } else {
      Jevil.stopLoading()
      Jevil.alert('일시적 네트워크 오류가 발생하였습니다. 다시 시도해주세요.')
    }
  })
}

function add(){
  let hasPicture = true
  let hasVideo = true
  if(data.list.length-1 < 10){
    Jevil.camera(
      {
        startFront:false,
        hasPicture : hasPicture,
        hasVideo : hasVideo,
        minSec : 3,
        maxSec : 60,
        ratio : 1.0
      },
      function(result){
        if(result && result.r) {
          if(result.image) {
            data.list.push({
              type:'image',
              image:result.image
            })
          } else if(result.video){
            data.list.push({
              type:'video',
              image:result.preview,
              video:result.video
            })
          }
          data.list[0].camera_text = (data.list.length-1) + '/'+10
          Jevil.update()
        }
    })
  }else{
    Jevil.alert('사진 10장 이상 업로드 할 수 없습니다.') 
  }
}


function remove(thisData) {
  let newList = []
  for(let i=0;i<data.list.length;i++){
    if(data.list[i].image != thisData.image){
      newList.push(data.list[i])
    }
  }

  data.list = newList
  data.list[0].camera_text = (data.list.length-1) + '/'+10
  Jevil.update()
}

