const user__type = data.user__type;

update()

function update() {
  Jevil.startLoading()
  Jevil.getThen('/api/product/detail/'+data.id, function(res){
    if(!res || !res.r) {
      Jevil.alertFinish(res.msg?res.msg:'일시적 네트워크 오류입니다. 잠시 후 다시 시도해주세요')
      return
    }

    data = res;
    data.user__type = user__type;

    if(data.spoon_count && data.spoon_count > 0){
      data.spoon_count = comma(data.spoon_count)
      data.show_spoon = 'Y'
    }

    //만나 반찬의 다른 아이템 보기 보이는지 여부
    data.recommend = data.brandOtherProducts.length > 0 ? 'Y' : 'N'
    data.recommend_title_text = data.business_name ? data.business_name + '의 다른 반찬은 어떠세요?' : data.name + '의 다른 반찬은 어떠세요?'
    data.brandOtherProducts.map(m=>{
      if(m.name.length > 8) {
        m.name__ellipsis = m.name.substring(0, 8) + '...';  
      }else{
        m.name__ellipsis = m.name
      }
      m.price_name = comma(m.price)+'원'
    })
    //푸터 free / not_free노드 보이기
    if(data.price > 0) {
      data.free = 'N'
      data.not_free = 'Y'
      data.price_comma = comma(data.price)
    } else {
      data.free = 'Y'
      data.not_free = 'N'
      // data.free_text = data.status == '나눔완료' ? '나눔완료' : '약속잡기';
      data.free_text = data.status == '나눔완료' ? 'Y' : 'N';
      // Jevil.alert(JSON.stringify(data.free_text, null, 2))
    }

    Jevil.getThen('/api/product/review/'+data.id, function(review){
      Jevil.stopLoading()
      data.review_list = review.list;
      data.review_list.map(m=>{
        m.starRating = m.rate
        Jevil.starRatingUpdate(m)
      })

      //만나 반찬의 리뷰 22건 부분이 보이는지 여부
      data.review_title = data.review_list.length == 0 ? 'N' : 'Y'
