function append(){
  Jevil.startLoading()
  Jevil.getThen('/api/cart/list', function(res) {
    if(res){
      Jevil.stopLoading()
      res.list.map(item => {
        data.shopname = item.member.business_name ? item.member.business_name : item.member.name
        item.product.checked = 'Y'
        item.product.qty = parseInt(item.qty) +''
        item.product.mainimage = item.product.media[0].image
        item.product.price__text = comma(item.product.price) + '원';
        data.cartItem.push(item.product)
        totalPrice(data.cartItem)
      })
      data.cartItem.length == 0 ? data.first = 'Y' : data.first = 'N';
      Jevil.update()
    } else {
      Jevil.alert('일시적 오류가 발생하였습니다 다시 시도해주세요')
    }
  })

  //상세주소가 입력되어있는지 체크
  if(data.user__type == '일반'){
    Jevil.getThen('/api/my', function(my){
      if(my && my.r){
        data.my_road_address_name = my.road_address_name;
        data.my_address_name = my.address_name;
        if(data.my_road_address_name.length == 0 && data.my_address_name.length == 0){
          data.my_address_check = 'N'
        }
      }
    }); 
  }

  Jevil.update()
}

function check(){
  if(data.check == 'Y'){
    data.check = 'Y'
    data.cartItem.map(item => {
      item.checked = "Y"
    })
  }
  else {
    data.check = 'N';
    data.cartItem.map(item => {
      item.checked = "N"
    })
  }
  totalPrice(data.cartItem)
  Jevil.update()
}

function check_cart_item(thisData){
  if(thisData.checked == "Y"){
    thisData.checked = "N"
    data.check = "N"
  }else{
    let checkData =[]
    thisData.checked = "Y"
    data.cartItem.map(item => {
      item.checked === "Y" ? checkData.push(item) : null;
    });
    checkData.length == data.cartItem.length ? data.check = "Y" : null;
  }
  totalPrice(data.cartItem)
  Jevil.update()
}

// //수량 마이너스
function itemMinus(thisData){
  if(thisData.qty > 1){
    newQty = parseInt(thisData.qty) - 1 + ''
    Jevil.getThen('/api/cart/update/'+thisData.id+'/'+newQty, function(res) {
      if(res && res.r){
        thisData.qty = newQty
        totalPrice(data.cartItem)
        Jevil.update()
      }else{
        Jevil.alert('일시적 오류가 발생하였습니다 다시 시도해주세요')
      }
    });

  }else{
    Jevil.alert('주문 가능한 최소\n수량은 1개 입니다.')
  }
  Jevil.update()
}

// //수량 추가
function itemPlus(thisData){
  newQty = parseInt(thisData.qty) + 1 + ''
  Jevil.getThen('/api/cart/update/'+thisData.id+'/'+newQty, function(res) {
    if(res && res.r){
      thisData.qty = newQty
      totalPrice(data.cartItem)
      Jevil.update()  
    }else{
      Jevil.alert('일시적 오류가 발생하였습니다 다시 시도해주세요')
    }
  });
}

// //장바구니 삭제
function removeItem(thisData){
  Jevil.getThen('/api/cart/remove/'+thisData.id, function(res) {
    if(res && res.r){
      const idx = data.cartItem.findIndex(item => {
        return item.id == thisData.id
      });
      data.cartItem.splice(idx,1);    
      totalPrice(data.cartItem)
      if(data.cartItem.length == 0){
        data.first = 'Y';
        Jevil.update()
      }
    }
  });

}

function totalPrice(items){
  let num = 0
  items.map(item => {
    item.price_name = comma(item.price * item.qty)+'원'
    item.checked == 'Y' ? num = num + (item.price* item.qty) : null;
  });
  num = comma(num)
  data.totalPrice = '총 '+num+'원'
  Jevil.update()
}

function complete(){
  if(data.my_address_check == 'N'){
    Jevil.confirm('상세주소가 입력되지 않았습니다. 설정페이지로 이동하시겠습니까?', '예', '아니오', function(yes){
      if(yes){
        Jevil.go('setting');  
      }
    })
    return
  }

  let checkedId = '' 
  let checkedQty = ''
  data.cartItem.map(item => {
     if(item.checked === "Y"){
        checkedId += item.id + ','
        checkedQty += item.qty + ','
     }
  });
  checkedId = checkedId.substring(0, checkedId.length - 1);
  checkedQty = checkedQty.substring(0, checkedQty.length - 1);
  Jevil.go('web', {title:'주문', url:'/checkout?product_id='+checkedId+'&qty='+checkedQty})
}

