      res.today == 0 && res.total == 0 ? data.price_box = 'Y' : data.price_box = 'N'
      Jevil.update()
    }
  })
}

function tab1() {
  data.subtab='1'
  updateProduct()
}
function tab2() {
  data.subtab='2'
  updateProduct()
}
function tab3() {
  data.subtab='3'
  updateProduct()
}
function tab4() {
  data.subtab='4'
  updateProduct()
}

function updateProduct() {
  let url = '/api/product/list'
  if(data.subtab=='1')
    url = '/api/order/sell/history?type=payment_complete'
  else if(data.subtab=='2')
    url = '/api/order/sell/history?type=ready'
  else if(data.subtab=='3')
    url = '/api/order/sell/history?type=complete'
  else if(data.subtab=='4')
    url = '/api/product/list?type=sell'

  Jevil.startLoading()
  Jevil.getThen(url, function(res) {
    Jevil.stopLoading()
    if(res && res.r) {
      data.list = res.list
      data.list.map(m=>{
        if(data.subtab=='1' || data.subtab=='2' || data.subtab=='3') {
          m.type = 'order'
          m.amount_text = comma(m.total_amount)+'원'

          //접수, 거부 버튼 보임 - block rule에 추가 조건(status == 'payment_complete') 있음, 좋은 방식 아님 여기에 그냥 show_btn_accept =='Y'로 설정해야함
          if(m.status == 'payment_complete')
            m.show_btns = 'Y'


          if(m.delivery_status == '직접수령' && m.status == 'ready') {
            m.show_btns = 'Y'
            m.show_btn_pickup = 'Y'
          } else if(m.delivery_info) {
            m.delivery_price_text = comma(m.delivery_info.price)+'원'
            if(m.delivery_info.delivery_member_no){
              m.show_delivery = 'Y'
              m.delivery_member_phone_text = phone(m.delivery_member_phone)
            }

            if(m.status == 'ready') {
              if(m.delivery_info.delivery_type == '미정') {
                m.show_btn_apply_delivery = 'Y'
                m.show_btn_apply_rider = 'Y'
                m.show_btns = 'Y'
              } else if(m.delivery_info.status == '배송중'){
                m.show_btn_pickup = 'Y'
                m.show_btns = 'Y'
              } else if(m.delivery_info.status == '배송준비중'){
                m.btn_delevery_cancel = 'Y'
                m.show_btns = 'Y'
                m.delivery_desc = '배송상태 : ' + m.delivery_info.delivery_type + ' '  + m.delivery_info.status
                m.show_delivery_desc = 'Y'
              } else {
                m.delivery_desc = '배송상태 : ' + m.delivery_info.delivery_type + ' '  + m.delivery_info.status
                m.show_delivery_desc = 'Y'
              }
            }

            if(m.status == 'complete') {
              m.show_btns = 'N'
            }
          }


        } else if(data.subtab=='4')
          m.type = 'product_card_price'
        if(parseInt(m.price) > 0)
          m.price_name = comma(m.price) + '원'
        else if(m.amount)
          m.price_name = comma(m.amount) + '원'
      })
      if(data.list.length == 0){
        data.first = 'Y';
        if(data.subtab == '1'){
          data.first_text = '대기 중인 주문이 없습니다.';
        }else if(data.subtab == '2'){
          data.first_text = '접수 중인 주문이 없습니다.';
        }else if(data.subtab == '3'){
          data.first_text = '완료된 주문이 없습니다.';
        }else if(data.subtab == '4'){
          data.first_text = '판매 내역이 없습니다.';
        }

      }else{
        data.first = 'N';
      }
      Jevil.update()
    }
  })
}


function order_accept(thisData) {
  Jevil.startLoading()
  Jevil.getThen('/api/order/sell/ready/' + thisData.id, function(res) {
    Jevil.stopLoading()
    if(res && res.r) {
      updateProduct()  
    }
  })
}

function order_cancel(thisData) {
  Jevil.startLoading()
  Jevil.getThen('/api/order/sell/cancel/' + thisData.id, function(res) {
    Jevil.stopLoading()
    if(res && res.r) {
      updateProduct()  
    } else if(res){
      Jevil.alert(res.msg)
    } else {
      Jevil.alert('일시적인 네트워크 오류입니다')
    }
  })
}

function cancel_delevery(thisData){
  Jevil.startLoading()
  Jevil.getThen('/api/order/delivery/cancel/' + thisData.id, function(res) {
    Jevil.stopLoading()
    if(res && res.r) {
      updateProduct()  
    } else if(res){
      Jevil.alert(res.msg)
    } else {
      Jevil.alert('일시적인 네트워크 오류입니다')
    }
  })
}

function apply_delivery(thisData) {
  Jevil.startLoading()
  Jevil.postThen('/api/order/sell/delivery/apply', { delivery_id : thisData.delivery_id , delivery_type : '뚜벅배송'}, function(res) {
    Jevil.stopLoading()
    if(res && res.r) {
      updateProduct()  
    }
  })
}

function apply_rider(thisData) {
  Jevil.alert('라이더 호출 준비중입니다')
  return;
