Jevil.chat_up = function (down) {
  Jevil.startLoading()
  let url = '/api/chat/up/' + data.chat_room_id + '/' + data.up_chat_id
  Jevil.getThen(url, function(res) {
    Jevil.stopLoading()
    if(res && res.r) {
      data.chat_room_me = res.me
      res.list.map(m=> {
        data.chat_list.splice(0, 0, m);
        data.up_chat_id = m.id
      })
      Jevil.update()

      if(down && data.chat_list.length > 0)
        Jevil.scrollTo('chat_list', data.chat_list.length - 1, true)
    }      
  })
}

Jevil.chat_down = function () {
  if(data.chat_list.length == 0)
    data.down_chat_id = 0
  else
    data.down_chat_id = data.chat_list[data.chat_list.length - 1].id
  let url = '/api/chat/down/' + data.chat_room_id + '/' + data.down_chat_id
  Jevil.getThen(url, function(res) {
    if(res && res.r) {
      res.list.map(m=> {
        if(m.id > data.down_chat_id) {
          data.chat_list.push(m);
          data.down_chat_id = m.id
        }
      })

      if(data.chat_list.length > 0)
        Jevil.scrollTo('chat_list', data.chat_list.length - 1)

      Jevil.update()
    }      
  })
}

Jevil.chat_message = function () {
  Jevil.chat_down()
}

