    Jevil.getThenWithHeader('https://dapi.kakao.com/v2/local/search/address.json?query='+encodeURI(data.road_address_name?data.road_address_name:data.address_name), {
      'Authorization': 'KakaoAK 0aac1d94231563eccc5d3f47d9a726a7'
    }, function(res){
      if(res) {
        data.address_post = res.documents[0].road_address?res.documents[0].road_address.zone_no:'-'
        data.address_x = res.documents[0].x
        data.address_y = res.documents[0].y
        data.address_detail = data.address_input2
        Jevil.popupClose(true)
      }
    })
  }
}

